import React, { Component } from 'react';
import './Directory.scss';

import MenuItem from '../MenuItem/MenuItem';

import { createStructuredSelector } from 'reselect';
import { selectDirectorySections } from '../../Redux/Directory/directorySelector';

class Directory extends Component {
  constructor(props) {
    super(props);
    this.state = {
      clothesMenuSections : [
        {
          id: 1,
          title: 'HATS',
          image: hatsimage,
          position: '50% 37%',
          size: '470px auto',
          linkUrl: 'hats'
        },
        {
          id: 2,
          title: 'JACKETS',
          image: jacketsimage,
          position: '50% 37%',
          size: '470px auto',
          linkUrl: ''
        },
        {
          id: 3,
          title: 'SNEAKERS',
          image: sneakersimage,
          position: 'center',
          size: '470px auto',
          linkUrl: ''
        }
      ],

      genderMenuSections: [
        {
          id: 4,
          title: "WOMEN",
          image: femaleimage,
          position: '50% 30%',
          size: '705px auto',
          linkUrl: ''
        },
        {
          id: 5,
          title: "MEN",
          image: maleimage,
          position: '50% 10%',
          size: '705px auto',
          linkUrl: ''
        }
      ]
    }
  }
  render() {
    return (
      <div className="homepage-directory">
        <div className="homepage-clothes-menu">
          {
            this.state.clothesMenuSections.map(({ id, ...menuProps }) => {
              return(
                <MenuItem key={id} {...menuProps} />
              )
            })
          }
          
        </div>
        <div className="homepage-gender-menu">
          {
            this.state.genderMenuSections.map(({id, ...menuProps}) => {
              return(
                <MenuItem key={id} {...menuProps} />
              )
            })
          }    
        </div>  
      </div>
    );
  }
}

export default Directory;
