import React, { Component } from 'react';
import './Homepage.scss'
import ClothesMenuItem from '../../components/clothesMenuItem/ClothesMenuItem';
import GenderMenuItem from '../../components/genderMenuItem/GenderMenuItem';
import sneakersimage from "./images/SNEAKERS.jpg";
import jacketsimage from "./images/jackets.jpg";
import hatsimage from "./images/hats.jpg";

class Homepage extends Component {
  constructor(props) {
    super(props);
    this.state = {
      clothesMenuSections : [
        {
          id: 1,
          title: 'HATS',
          image: hatsimage,
          position: '50% 37%'
        },
        {
          id: 2,
          title: 'JACKETS',
          image: jacketsimage
        },
        {
          id: 3,
          title: 'SNEAKERS',
          image: sneakersimage
        }
      ],

      genderMenuSections: [
        {
          id: 4,
          gender: "WOMEN",
          image: sneakersimage
        },
        {
          id: 5,
          gender: "MEN",
          image: ''
        }
      ]
    }
  }
  render() {
    return (
      <div className="homepage-directory">
        <div className="homepage-clothes-menu">
          {
            this.state.clothesMenuSections.map(({title, id, image, position}) => {
              return(
                <ClothesMenuItem title={title} key={id} image={image} position={position} />
              )
            })
          }
          
        </div>
        <div className="homepage-gender-menu">
        {
          this.state.genderMenuSections.map(({gender, id, image}) => {
            return(
              <GenderMenuItem gender={gender} key={id} image={image} />
            )
          })
        }    
        </div>  
      </div>
    );
  }
}

export default Homepage;
