export const addItemsToCart = (currentCartItmes, itemToAdd) => {
  let newCartItmes = []
  if (currentCartItmes.length) {
    for (let item of currentCartItmes) {
      item.id === itemToAdd.id
        ? newCartItmes.push({...item, quantity: item.quantity + 1})
        : newCartItmes.push({...itemToAdd, quantity: 1})
    }
  } else {
     newCartItmes.push({...itemToAdd, quantity: 1})
  }

  return newCartItmes
}
