export const addItemsToCart = (currentCartItmes, itemToAdd) => {
  if (currentCartItmes.length) {
    for (let item of currentCartItmes) {
      if (item.id === itemToAdd.id) {
        return [...currentCartItmes, {...item, quantity: item.quantity + 1}]
      }  
    }
  } else {
    return [...currentCartItmes, {...itemToAdd, quantity: 1}]
  }
}
