import { takeLatest, all, call, put } from "@redux-saga/core/effects";
import { auth, createUserProfileDocument, googleProvider,  } from "../../firebase/firebase.utils";
import { SignInSuccess, SignInError } from "./userAction";

import { EMAIL_SIGN_IN_START, GOOGLE_SIGN_IN_START } from './userActionTypes'

export function* getSnapshotFromAuth( userAuth ) {
  try {
    const userRef = yield call(createUserProfileDocument, userAuth);
    const userSnapShot = yield userRef.get();
    yield put(SignInSuccess({ id: userSnapShot.id, ...userSnapShot.data() }))
  } catch (error) {
    yield put(SignInError(error.message))
  }
}



// singn in with google

export function* signInWithGoogle() {
  try {
    const { user } = yield auth.signInWithPopup(googleProvider); 
    yield getSnapshotFromAuth(user)
  } catch (error) {
    yield put(SignInError(error.message))
  }
}

export function* onGoogleSignInStart() {
  yield takeLatest(
    GOOGLE_SIGN_IN_START, 
    signInWithGoogle
  )
}

//sigin in with emaail and password

export function* signInWithEmail({ payload: { email, password } }) {
  try {
    const { user } = yield auth.signInWithEmailAndPassword(email, password); 
    yield getSnapshotFromAuth(user)
  } catch (error) {
    yield put(SignInError(error.message))
  }
}

export function* onEmailSignInStart() {
  yield takeLatest(
    EMAIL_SIGN_IN_START, 
    signInWithEmail
  )
}

export function* userSagas() {
  yield all([
    call(onGoogleSignInStart),
    call(onEmailSignInStart)
  ])
}
