import React, { Component } from 'react';
import './SignIn.scss';
import TextField from '@mui/material/TextField';
import Button from '@mui/material/Button';
import { styled } from '@mui/system';


import { googleSignInStart, emailSignInStart } from '../../Redux/user/userAction'

import { connect } from 'react-redux';

// $accent-color: linear-gradient(90deg,#ed145b 0,#7b31f4);
const MyComponent = styled(TextField)({
  width: '100%',
              marginBottom: '25px',
              '& > .MuiFilledInput-root': {
                borderRadius: '0px',
                '&:hover:before': {
                  borderBottom: '2px solid #e00bcb !important',
                } 
              }
           
})

class SignIn extends Component {
  constructor(props) {
    super(props);
    this.state = {
      email: '',
      password: ''
    }
  }

  handelChange = event => {
    const { value, type } = event.target
    this.setState({ [type] : value }, () => console.log(this.state))
  }

  handelSubmit = async event => {
    event.preventDefault();
    const { emailSignInStart } = this.props;
    const { email, password } = this.state;

    emailSignInStart(email, password)

    // try {

    //   await auth.signInWithEmailAndPassword(email, password);
    //   this.setState({ email: '', password: '' });

    // } catch(error) {

    //   console.error(error);
    //   alert('somthing wrong with your credentials')

    // }
    // this.setState({ email: '', password: '' });
    // document.getElementById('filled-basic1').value = '';
    // document.getElementById('filled-basic2').value = '';
  }

  render() {
    const { googleSignInStart } = this.props;
    return (
      <div className='sign-in active'>
        <h1 className='sign-in-title'>Sign in</h1>
        <form 
          className="signin-form" 
          noValidate 
          autoComplete='off'
          onSubmit={this.handelSubmit}
        >
          <div className='signin-form-email-area'>
            <MyComponent 
                type='email'   
                id="filled-basic1" 
                label="Email" 
                variant="filled"
                required
                onChange={this.handelChange}
              />
          </div>
          <div className='signin-form-password-area'> 
            <MyComponent 
              type='password'    
              id="filled-basic2" 
              label="password" 
              variant="filled"
              required
              onChange={this.handelChange}
            />
          </div>
          <div className='note'>{`register >>`}</div>
          <div className='signin-form-buttons'>
          <Button
            sx={{
              marginTop: '30px',
              borderRadius: '0px',
              backgroundColor: 'rgba(224, 11, 203, 1)',
              '&:hover': {
                backgroundColor: 'black',
              }
            }}
            variant="contained" 
            size="large"
            type='submit'
            onClick={this.handelSubmit}
          >
            Sign In
          </Button>
          <Button
            sx={{
              marginTop: '30px',
              borderRadius: '0px',
              backgroundColor: '#4285F4',
              '&:hover': {
                backgroundColor: 'white',
                color: 'black',
                boxShadow: 'inset 0px 0px 0px 2px black',
                boxSizing: 'border-box'
              }
            }}
            variant="contained" 
            size="large"
            onClick={googleSignInStart}
          >
            sign in with google
          </Button>        
          </div>     
        </form>
      </div>
    )
  }
  
}

const mapDispatchtoProps = dispatch => ({
  googleSignInStart: () => dispatch(googleSignInStart()),
  emailSignInStart: (email, password) => dispatch(emailSignInStart({ email, password }))
})

export default connect(null, mapDispatchtoProps)(SignIn);
