import React from 'react';
import './ItemCard.scss';
import Button from '@mui/material/Button';
import Slide from '@mui/material/Slide';
const ItemCard = ({ imageUrl, name, price }) => {
  const [checked, setChecked] = React.useState(false);
  const handleHover = (flag) => {
    setChecked(flag);
  }
  return (
    <div className='item-card'>
      <div 
        onMouseEnter={() => setChecked(true)}
        onMouseLeave={() => setChecked(false)}
        className='background-card-image'
        style={{
          height: '350px',
          width: '100%',
          backgroundImage: `url(${imageUrl})`    
        }}
      >
      <Slide timeout={300} direction="up" in={checked} mountOnEnter unmountOnExit>
      <Button
        className='add-cart-button'
        sx={{
          width: '300px',
          height: '50px',
          display: 'block',   
          borderRadius: '0px',
          backgroundColor: 'black',
          '&:hover': {
            backgroundColor: 'white',
            color: 'black',
            boxShadow: 'inset 0px 0px 0px 2px black',
            boxSizing: 'border-box'
          }
        }}
        variant="contained" 
        size="large"
        type='submit'
       
    >
      add to cart
    </Button>
    </Slide>
      </div>
      <div className='item-colors'>colors</div>
      <div className='item-card-footer'>
        <p className='item-name'>{name}</p>
        <p className='item-price'>{`${price}$`}</p>
      </div> 
    </div>
  )
}

export default ItemCard;
