import { takeLatest, all, call, put } from "@redux-saga/core/effects";
import { auth, createUserProfileDocument, googleProvider } from "../../firebase/firebase.utils";
import { googleSignInError, googleSignInSuccess } from "./userAction";

import { GOOGLE_SIGN_IN_START } from './userActionTypes'


export function* signInWithGoogle() {
  try {
    const { user } = yield auth.signInWithPopup(googleProvider); 
    const userRef = yield call(createUserProfileDocument, user);
    const userSnapShot = userRef.get();
    yield put(googleSignInSuccess({ id: userSnapShot.id, ...userSnapShot.data() }))
  } catch (error) {
    yield put(googleSignInError(error.message))
  }
}

export function* onGoogleSignInStart() {
  yield takeLatest(
    GOOGLE_SIGN_IN_START, 
    signInWithGoogle
  )
}

export function* userSagas() {
  yield all([
    call(onGoogleSignInStart)
  ])
}
